mapper.select:
SELECT q.qna_no, q.member_no, q.qna_category, q.qna_title, q.qna_secret, q.create_date, m.member_name FROM qna q LEFT JOIN member m ON q.member_no = m.member_no
q.member_no = #{memberNo} AND qna_category = #{qnaCategory}
q.member_no = #{memberNo}
ORDER BY create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT m.member_no, m.member_name, ac.accom_name, r.room_name, rev.review_content, rev.review_rank, rev.review_recommend, rev.create_date FROM member m JOIN reserve re ON re.member_no = m.member_no JOIN payment p ON re.reserve_no = p.reserve_no JOIN review rev ON rev.payment_no = p.payment_no JOIN room r ON r.room_no = re.room_no JOIN accom ac ON r.accom_no = ac.accom_no
m.member_no = #{memberNo} AND rev.review_recommend = #{reviewRecommend}
m.member_no = #{memberNo}
ORDER BY rev.create_date DESC LIMIT #{beginRow}, #{rowPerPage}

SELECT COUNT(*) 
FROM member 
WHERE 
	member_id = #{memberId}
	AND 
	member_certifycode = #{memberCertifycode}

SELECT member_active 
FROM member
WHERE member_id = #{memberId}

mapper.delete:
DELETE FROM member
WHERE 
member_id=#{memberId}
AND
member_pw=#{memberPw}

mapper.select:
SELECT COUNT(member_id) FROM(
	SELECT member_id FROM member WHERE member_id = #{memberId}
	UNION
	SELECT member_id FROM member_out WHERE member_id = #{memberId}
	) AS u
